func TestInitSdk(t *testing.T) {
	sdk.InitSDK("test.cocosbcx.net", 80, false)
	sdk.Wallet.ImportAccount("gggg1", "12345678")
	sdk.Wallet.SetDefaultAccount("gggg1", "12345678")
	t.Log(rpc.GetDynamicGlobalProperties())
}

